for(var slider=document.querySelectorAll(".gadget_slide"),button=document.querySelectorAll(".gadget_toggle"),i=0;i<button.length;i++)button[i].addEventListener("click",function(t){t.target.classList.add("current-toggle");for(var e=t.target.getAttribute("data-gadget"),l=0;l<slider.length;l++)slider[l].getAttribute("data-gadget")===e?slider[l].classList.remove("visually-hidden"):(slider[l].classList.add("visually-hidden"),button[l].classList.remove("current-toggle"))});